import React from "react";
import {
  Document,
  Page,
  Text,
  View,
  Svg,
  Link,
  Path,
} from "@react-pdf/renderer";

import { gradeOptions, sliderValueToLanguageLevel } from "@/helpers/constants";

import { styles } from "./styles";

const renderSkills = (skills, languageLevel = false, dots = true) => {
  if (skills && skills.length > 0) {
    return (
      <View style={styles.skillsList}>
        {skills.map((skill, index) => (
          <View
            key={index}
            style={{
              display: "flex",
              flexDirection: "row",
              alignItems: "flex-start",
              gap: 5,
            }}
          >
            {dots && (
              <View
                style={{
                  display: "block",
                  marginTop: 7,
                  height: 3,
                  width: 3,
                  backgroundColor: "black",
                  opacity: 0.6,
                  borderRadius: "50%",
                }}
              />
            )}
            <Text style={styles.skill}>
              {skill?.name || skill}{" "}
              {languageLevel &&
                ` - ${sliderValueToLanguageLevel[+skill.level]}`}
            </Text>
          </View>
        ))}
      </View>
    );
  } else {
    return <Text />;
  }
};

